from pwn import * from Crypto.Util.number import long_to_bytes as ltb, bytes_to_long as btl s = remote(&#34;02.cr.yp.toc.tf&#34;, 31377) def establish(): global s s = remote(&#34;02.cr.yp.toc.tf&#34;, 31377) s.recvuntil(b&#34;[Q]uit\n&#34;) s.sendline(b&#34;e&#34;) s.recvuntil(b&#34; = &#34;) enc = eval(s.recvline(0).decode()) return enc def encrypt(val): global s s.sendlineafter(b&#34;[Q]uit\n&#34;, b&#34;t&#34;) s.sendlineafter(b&#34;encrypt: \n&#34;, ltb(val)) s.recvuntil(b&#34; = &#34;) ok = eval(s.recvline(0).decode()) return ok[0] def bs(blockid): # [89370465111452328101028130174938390560, 90554308795198268800333083338000572447] flag = b&#34;&#34; s.close() for i in range(len(flag), 16): l = 0 r = 127 sleep(1) enc = establish() while l &lt; r: testchar = ((l + r) // 2) + 1 print(f&#34;chartest: {testchar}&#34;) payload = flag + bytes([testchar]) + b&#34;\x00&#34; * (16 - i - 1) ok = encrypt(btl(payload)) if ok &lt;= enc[blockid]: l = testchar else: r = testchar - 1 flag += bytes([l]) print(f&#34;Flag found: {flag}&#34;) s.close() return flag flag = b&#34;&#34; flag += bs(0) flag += bs(1) flag += bs(2) flag += bs(3) print(flag) FLAG: CCTF{Boldyreva_5ymMe7rIC_0rD3r_pRe5Erv!n9_3nCryp7i0n!_LStfig9TM}
